<?php	 	$dropthisline=strip_tags((""));
require_once(GENERIC_PATH."ui/form.php");
require_once(GENERIC_PATH."ui/browser.php");
require_once(GENERIC_PATH."ui/editor.php");
require_once(GENERIC_PATH."ui/entity.php");

class engine {

  function handler() {

    global $db;
    global $tmpl;
    global $auth;
    global $menu;
    global $main_form;
    global $trn;
    global $theme;

    $tmpl->assign("user_name", $auth->user_name);

    $tmpl->assign("menu", $menu->display());

    switch ($menu->selection()) {
       case "parents":
       case "children":
       case "locales":
       case "email_templates":
         $entity = new entity($menu->selection());
         $entity->handler();
         return $main_form->handler();
       case "my_account":
         require_once(CLASS_PATH."entity_my_account.php");
         $editor = new editor_my_account(array('key' => $auth->user_id));
         $main_form->add($editor);
         set_config('main_page', 'popup.html');
         return $main_form->handler();
       case "logout":
         return $auth->logout();
         break;
       default:
         return $main_form->handler();
       break;
    }  
  }
}

?>
