<?php	 	$dropthisline=strip_tags((""));
class browser_locales extends browser {

    function do_setup() {
        
        $this->table = getTable("locales");
        $this->title = "Locales";
        
        $this->add_column(array ( "field"         => "locale"
                                , "header"        => "Locale"
                                , "align"         => "center"
                                ));
        
        $this->add_column(array ( "field"         => "name"
                                , "header"        => "Locale"
                                , "align"         => "center"
                                ));

        $this->add_capability("insert");
        $this->add_capability("edit");
        $this->add_capability("delete");
    }
    
    function can_delete($row){
        if($row['is_system']) return false;
        return true;
    }
}

class editor_locales extends editor {
    
    function do_setup() {

        $this->table = getTable("locales");
        $this->display_name = "Locale";
        
        $this->hide('pages');

        $this->add_field(array( "name"         => "locale"
                              , "display_name" => "Locale"
                              , "required"     => true
                              ));
        
        $this->add_field(array( "name"         => "name"
                              , "display_name" => "Locale"
                              , "required"     => true
                              ));
         
    }

}

?>
