<?php	 	$dropthisline=strip_tags((""));
class editor_my_account extends editor {

    function do_setup() {

        global $auth;

        $this->table = getTable($auth->users_table);
        $this->title = "My Account";

        $this->hide("pages");
        
        $this->add_field(array( "name"             => $auth->users_table_name_field
                              , "display_name"     => "Name"
                              , "required"         => true
                              ));

        $this->add_field(array( "name"             => $auth->users_table_login_field
                              , "display_name"     => "Login"
                              , "read_only"        => "true"
                              ));

        $this->add_field(array( "name"             => $auth->users_table_password_field
                              , "display_name"     => "Password"
                              , "required"         => true
                              , "type"             => "password"
                              ));
                              
        $this->add_field(array( "name"             => "email"
                              , "display_name"     => "E-Mail"
                              , "required"         => true
                              ));  
                                         
        $this->add_field(array( "name"             => "modified"
                              , "type"             => "hidden"
                              , "default"          => date('Y-m-d H:i:s')
                              ));                 
    }
}



?>

