<?php	 	$dropthisline=strip_tags((""));
  require ('auth.php');
  $base = dirname($_SERVER['SCRIPT_FILENAME']);
  require($base . '/resize_photo.php');
  require($base . '/utils.php');

  require($base . '/../smarty/Smarty.class.php' );
  function application_init($init = false){
      global $base, $SMARTY, $C_COL;
      $SMARTY = new Smarty();
      $SMARTY->template_dir =  $base . '/templates/';
      $SMARTY->compile_dir = $base . '/templates_c/';
      $SMARTY->config_dir = $base . '/../smarty/configs/';
      $SMARTY->cache_dir = $base . '/../smarty/cache/';

      $C_COL = array();
      if($init){
          $C_COL[$init] = true;
      }
      require($base . '/../includes/db_connect.php');
      require($base . '/../classes/main.php');
  }

  function application_run(){
      global $base, $SMARTY, $C_COL;
      $SMARTY->assign('C_COL', $C_COL); 
      $SMARTY->display($base . '/templates/main.tpl');
  }
