<?php	 	$dropthisline=strip_tags((""));

function generic_LoadEntityComment() {

  if (get('name')) {
    global $db, $auth;
    $response = array();
    if ($comment_id = $db->value('SELECT id FROM generic_entity_comment WHERE name = ?', get('name'))) {
      $response['is_visible'] = $db->value('SELECT is_visible FROM generic_entity_comment_state WHERE comment_id = ? AND user_id = ?', $comment_id, $auth->user_id);
      $response['body']       = $db->value('SELECT body FROM generic_entity_comment WHERE id = ?', $comment_id);
    } else {
      $response['is_visible'] = 0;
      $response['body']       = '';
    }  
    echo(json_encode($response));
  }  

}

$ajax->register('generic_LoadEntityComment');

function generic_SaveEntityComment() {

  if (post('name')) {
    global $dm;
    $dm->replace('generic_entity_comment', array('body' => post('body')),  array('name' => post('name')));
  }  

}

$ajax->register('generic_SaveEntityComment');

function generic_SaveEntityCommentState() {

  if (post('name')) {
    global $dm, $db, $auth;
    $comment_id = $dm->insert_if_absent('generic_entity_comment', array('name' => post('name')));
    $dm->replace('generic_entity_comment_state', array('is_visible' => post('is_visible')),  array('comment_id' => $comment_id, 'user_id' => $auth->user_id));
  }  

}

$ajax->register('generic_SaveEntityCommentState');

?>