    $this->add_capability("sort");

    $this->add_column(array ( "field"    => "field_name"
                            , "header"   => "Field"
                            , "sortable" => true
                            , "width"    => 140
                            ));
    $this->add_column(array ( "field"    => "old_value"
                            , "header"   => "Old value"
                            , "sortable" => true
                            ));
    $this->add_column(array ( "field"    => "new_value"
                            , "header"   => "New value"
                            , "sortable" => true
                            ));

  }

