require_once(GENERIC_PATH."db/db.php");
require_once(GENERIC_PATH."db/db_odbc.php");
require_once(GENERIC_PATH."utils/utils.php");

class db_msaccess extends db_odbc {

  function db_msaccess($db_server, $db_name, $db_user, $db_password) {

    $db_dsn = "DRIVER=Microsoft Access Driver (*.mdb);DBQ=$db_name;UserCommitSync=Yes;Threads=3;SafeTransactions=0;PageTimeout=5;MaxScanRows=8;MaxBufferSize=2048;DriverId=281";
    parent::db_odbc($db_server, $db_dsn, $db_user, $db_password);

    $this->capabilities["query_limted_amount"] = true;
    $this->capabilities["query_from_offset"]   = true;
    
  }

  function limit($sql, $from, $count) {

    return $sql." LIMIT $from, $count";

  }

}

