<?php	 	$dropthisline=strip_tags((""));

require_once(GENERIC_PATH."db/db.php");
require_once(GENERIC_PATH."db/db_odbc.php");
require_once(GENERIC_PATH."utils/utils.php");

class db_sybase extends db_odbc {

  function db_sybase($db_server, $db_name, $db_user, $db_password) {

    $db_dsn=DB_DSN_SYBASE;

    parent::db_odbc($db_server, $db_dsn, $db_user, $db_password);

    $this->capabilities["last_id"]             = true;
    $this->capabilities["query_limted_amount"] = true;
    $this->capabilities["query_from_offset"]   = true;

  }

  function limit($sql, $from, $count) {

    if ($from==0)
      $from=1;
    $limit = "SELECT TOP ".$count." START AT ".$from; 	
    $sql = eregi_replace("select", $limit, $sql);
    return $sql;
  }

  function last_id() {

    return $this->value("SELECT @@IDENTITY AS last_id");

  }
  
  function internal_count($query) {

    $result = 0;
    while ($row = $this->next_row($query)) 
      $result++;
    return $result;

  }

  function addslashes($value) {

    return str_replace("'", "''", $value);

  }

}

?>