<?php	 	$dropthisline=strip_tags((""));

// check
if (!defined('GENERIC_PATH') or !defined('APPROOT_PATH')) 
  die('Not initialized');

if (get_config('db.connection_in_error')) {

  __finalyze_smarty_config();
  $tmpl->display(get_config('db.connection_error_page'));

} else {

  define('ENGINE_SCRIPT', 'engine.php');
  define('ENGINE_CLASS',  'engine');

  if (file_exists(CLASS_PATH.ENGINE_SCRIPT)) {
    $log->start('Run');
    require_once(CLASS_PATH.ENGINE_SCRIPT);
    $class_name = ENGINE_CLASS;
    $engine = new $class_name();
    $tmpl->assign('content', $engine->handler());
    $log->finish('Run');

    if (!CONSOLE_MODE && get_config('main_page')) {
      $log->start('Output');
      __finalyze_smarty_config();
      $tmpl->display(get_config('main_page'), get_config('cache_id'));
      $log->finish('Output');
    }
  }

}

?>