function smarty_function_popularity($params, &$smarty){
    extract($params);

    $pop = get_popularity($value, get_counter("total_popularity"));
    $out = '<img src="{$resources}pop'.$pop.'.gif" alt="'.$pop.'" width="51" height="8" border="0" />';
    $out = $smarty->fetch("string:".$out);
    
    if (!empty($assign)) {
        $smarty->assign($assign, $out);
    } else {
        return $out;
    }
}

