/**
 * Smarty file_exists modifier plugin
 *
 * Type:     modifier<br>
 * Name:     file_exists<br>
 * Purpose:  checks is file exists
 * @author Andrej Pismennyj <pav@itera.ws>
 * @version $Revision: 0.1 $
 * @param string
 * @return string
 */
function smarty_modifier_file_exists( $file, $default = null )
{

  if (!empty($file) && file_exists($file))
  	return true;
  else
  	return ($default?$default:false);

}

