/**
 * Smarty for_html modifier plugin
 *
 * Type:     modifier<br>
 * Name:     for_html<br>
 * Purpose:  regular expression search/replace. preparation for output
 * @param string
 * @param extra parameters. For example: "encode=true,class=product_link"
 * @return string
 */
function smarty_modifier_for_html($string, $extra = "")
{
    if ($extra){
      $extra = str_replace(' ', '', $extra);
      $options = array();
      $opts = split("[;,]", $extra);
      foreach ($opts as $opt){
        $tmp = split("[=]", $opt);
        $options[$tmp[0]] = $tmp[1];
      }
      $extra = $options;
    }
    return for_html($string, $extra);
}

