/**
 * Smarty hyperlink modifier plugin
 *
 * Type:     modifier<br>
 * Name:     hyperlink<br>
 * Purpose:  generate link using regnow feature and encode redirect url.
 * @param link
 * @param regnow_id
 * @param id
 * @return generated link
 */
function smarty_modifier_hyperlink($link, $regnow_id = 0, $id = -1) {
	global $engine;
    
    if (check_url($link)){
        $url = $engine->generate_link($link, $regnow_id, $id);
        return "<a href=\"".$url."\" onmouseover=\"window.status='".$link."'; return true\" onmouseout=\"window.status='';\" target=\"_blank\">".$link."</a>";
    } else return $link;
}

