/**
 * Smarty average rating modifier plugin
 *
 * Type:     modifier<br>
 * Name:     rating_stars<br>
 * Purpose:  calculate average rating
 * @author   Pavel Chipiga <pchipiga@itera.ws>
 * @version  1.0
 * @param string - id
 * @param table - rating table
 * @param key - rating table key
 * @return float
 */
 
function smarty_modifier_rating_avg($id, $table = "product_user_rating", $key = "product_id", $value = "value", $filter = ""){
    global $db;
    
    $sql = "SELECT SUM(".$value.")/COUNT(1) FROM ".$table." WHERE ".$key." = ?".($filter ? " AND ".$filter : "");
    $row = $db->row($sql, $id);
    //return round(array_pop($row));
    return array_pop($row);
}     

