/**
 * Smarty unique_words modifier plugin
 *
 * Type:     modifier<br>
 * Name:     unique_words<br>
 * Purpose:  remove word duplicates from string
 * @param string
 * @param delimiter
 * @param global = if false - remove only neighboor word duplicates 
 * @return string
 */
function smarty_modifier_unique_words($string, $delimiter = " ", $trim = ",;.", $global = false){
    $words = explode($delimiter, $string);
    if ($global) $words = array_unique($words);
    else {
        for($i=0; $i<count($words); $i++)
            if (isset($words[$i+1]) and trim($words[$i], $trim) == trim($words[$i+1], $trim)) unset($words[$i]);
    }
    
    return implode($delimiter, $words);
}

