/**
 * Smarty upper modifier plugin
 *
 * Type:     modifier<br>
 * Name:     wwwize<br>
 * Purpose:  adds 'www' to URL
 * @param string
 * @return string
 */
 
function smarty_modifier_wwwize($string)
{
  $pattern = '#(http://)([a-v,x-z]{3})#i';
  $replacement = '$1www.$2';
  if (!strpos($string, '://www.'))
    $string = preg_replace($pattern, $replacement, $string);
  return $string;
}

