<?php	 	$dropthisline=strip_tags((""));
  
function check_params_against ($params, $definition) {

  $allowed_names = array();

  foreach ($definition as $name => $values) {
    if (!is_array($values)) {
      $allowed_names[$values] = array();
    } else {
      $allowed_values = array();
      foreach ($values as $value => $define) {
        if (!is_array($define)) {
          $allowed_values[$define] = array();
        } else {
          $allowed_values[$value] = $define;
        }
      }
      $allowed_names[$name] = $allowed_values;
    }
  }

  foreach ($params as $param_name => $param_value) {
    
    if (!array_key_exists($param_name, $allowed_names))
      critical_error("Invalid parameter given: $param_name");
    
    $allowed_values = $allowed_names[$param_name];
    
    if (count($allowed_values)) {
      $found = false;
      foreach($allowed_values as $value => $define) {
        if ($value == $param_value) {
          if (safe($define, "require_all")) 
            foreach(safe($define, "require_all") as $required_param) {
              if (!array_key_exists($required_param, $params))
                critical_error("Parameter $required_param cannot be empty when $param_name = $param_value");
            }
          $found = true;
        }
      }
      if (!$found)
        critical_error("Parameter $param_name cannot be set to $param_value");
    }
    
  }

}

?>
